import re

with open(r''src\tdSynchManager\manager.py'', ''r'', encoding=''utf-8'') as f:
    lines = f.readlines()

# Track some sample methods
methods_to_check = [''__init__'', ''run'', ''_spawn_sync'', ''screen_option_oi_concentration'', ''generate_duplicate_report'', ''check_duplicates_multi_day'', ''clear_first_date_cache'', ''_validate_interval'']

current_group = None
method_groups = {}

for i, line in enumerate(lines):
    # Detect BEGIN
    if ''# (BEGIN)'' in line and i + 1 < len(lines):
        next_line = lines[i + 1]
        match = re.search(r''# ([A-Z][A-Z\s]+)$'', next_line)
        if match:
            current_group = match.group(1).strip()

    # Detect END
    elif ''# (END)'' in line:
        current_group = None

    # Check for methods
    elif current_group:
        for method in methods_to_check:
            if f''def {method}('' in line and method not in method_groups:
                method_groups[method] = current_group

print(''Sample methods verification:'')
print(''='' * 70)
for method in methods_to_check:
    group = method_groups.get(method, ''NOT FOUND'')
    print(f''{method:40} -> {group}'')
print(''='' * 70)
